! function($) {
	"use strict";
	$.fn.toTop = function(option) {
		var i = this,
			e = $(window),
			s = $("html, body"),
			n = $.extend({
				autohide: !0,
				offset: null,
				speed: 500,
				position: !0,
				right: 15,
				bottom: 30
			}, option);
		i.css({
			cursor: "pointer"
		}), n.autohide && i.css("display", "none"), n.position && i.css({
			position: "fixed",
			right: n.right,
			bottom: n.bottom
		}), i.click(function() {
			s.animate({
				scrollTop: 0
			}, n.speed)
		}), e.scroll(function() {
			var o = e.scrollTop();
			if(!n.offset) {
        if (o > 0 && n.autohide) {
          i.fadeIn(n.speed)
        } else if (n.autohide) {
          i.fadeOut(n.speed)
        }
        return
      }
			n.autohide && (o + $(window).height() > n.offset ? i.fadeIn(n.speed) : i.fadeOut(n.speed))
		})
		e.trigger('scroll')
	}
}(jQuery);
